```typescript
import { describe, it, expect, vi, beforeEach } from 'vitest';
import { NotificationService } from '../notifications-service';
import { ConsoleProvider } from '../../providers/console-provider';
import type { BrandingSettingsService, ContactSettingsService } from '@codex/platform-settings';

describe('NotificationService', () => {
  let service: NotificationService;
  let mockDb: any;
  let mockBrandingService: Partial<BrandingSettingsService>;
  let mockContactService: Partial<ContactSettingsService>;
  let mockProvider: ConsoleProvider;

  beforeEach(() => {
    mockDb = {
      query: {
        emailTemplates: {
          findFirst: vi.fn(),
        },
      },
    };

    mockBrandingService = {
      getSettings: vi.fn().mockResolvedValue({
        platformName: 'Test Platform',
        logoUrl: 'https://example.com/logo.png',
        primaryColor: '#000000',
        secondaryColor: '#ffffff',
      }),
    };

    mockContactService = {
      getSettings: vi.fn().mockResolvedValue({
        supportEmail: 'support@example.com',
        websiteUrl: 'https://example.com',
      }),
    };

    mockProvider = new ConsoleProvider();

    service = new NotificationService({
      db: mockDb,
      emailProvider: mockProvider,
      brandingService: mockBrandingService as BrandingSettingsService,
      contactService: mockContactService as ContactSettingsService,
      fromEmail: 'noreply@example.com',
      fromName: 'Test Sender',
      environment: 'test',
    });
