import { Hono } from 'hono';
import { procedure, createServiceRegistry } from '@codex/worker-utils';
import { createDbClient, schema } from '@codex/database';
import { eq, and, desc } from 'drizzle-orm';
import {
  createGlobalTemplateSchema,
  createOrgTemplateSchema,
  createCreatorTemplateSchema,
  updateTemplateSchema,
  listTemplatesQuerySchema,
  uuidSchema,
} from '@codex/validation';
import type { HonoEnv } from '@codex/shared-types';

const app = new Hono<HonoEnv>();

// ============================================
// GLOBAL TEMPLATES (Platform Owner Only)
// ============================================

// List global templates
app.get('/global', procedure({
  policy: { auth: 'required', roles: ['platform_owner'] },
  input: { query: listTemplatesQuerySchema },
  handler: async (ctx) => {
    const db = createDbClient(ctx.env);
    const { page, limit, status } = ctx.input.query;
    const offset = (page - 1) * limit;

    const where = and(
      eq(schema.emailTemplates.scope, 'global'),
      status ? eq(schema.emailTemplates.status, status) : undefined
    );

    const templates = await db.query.emailTemplates.findMany({
      where,
      limit,
      offset,
      orderBy: [desc(schema.emailTemplates.createdAt)],
    });

    return { data: templates };
  },
}));

// Create global template
app.post('/global', procedure({
  policy: { auth: 'required', roles: ['platform_owner'] },
  input: { body: createGlobalTemplateSchema },
  successStatus: 201,
  handler: async (ctx) => {
    const db = createDbClient(ctx.env);

    const [template] = await db.insert(schema.emailTemplates).values({
      ...ctx.input.body,
      scope: 'global',
      organizationId: null,
      creatorId: null,
      createdBy: ctx.user.id,
    }).returning();

    return { data: template };
  },
}));

// Get global template by ID
app.get('/global/:id', procedure({
  policy: { auth: 'required', roles: ['platform_owner'] },
  input: { params: { id: uuidSchema } },
  handler: async (ctx) => {
    const db = createDbClient(ctx.env);

    const template = await db.query.emailTemplates.findFirst({
      where: and(
        eq(schema.emailTemplates.id, ctx.input.params.id),
        eq(schema.emailTemplates.scope, 'global')
      ),
    });

    if (!template) {
      return ctx.json({ error: 'Template not found' }, 404);
    }

    return { data: template };
  },
}));

// Update global template
app.patch('/global/:id', procedure({
  policy: { auth: 'required', roles: ['platform_owner'] },
  input: { params: { id: uuidSchema }, body: updateTemplateSchema },
  handler: async (ctx) => {
    const db = createDbClient(ctx.env);

    const [template] = await db.update(schema.emailTemplates)
      .set(ctx.input.body)
      .where(and(
        eq(schema.emailTemplates.id, ctx.input.params.id),
        eq(schema.emailTemplates.scope, 'global')
      ))
      .returning();

    if (!template) {
      return ctx.json({ error: 'Template not found' }, 404);
    }

    return { data: template };
  },
}));

// Delete global template (soft delete)
app.delete('/global/:id', procedure({
  policy: { auth: 'required', roles: ['platform_owner'] },
  input: { params: { id: uuidSchema } },
  handler: async (ctx) => {
    const db = createDbClient(ctx.env);

    const [template] = await db.update(schema.emailTemplates)
      .set({ deletedAt: new Date() })
      .where(and(
        eq(schema.emailTemplates.id, ctx.input.params.id),
        eq(schema.emailTemplates.scope, 'global')
      ))
      .returning();

    if (!template) {
      return ctx.json({ error: 'Template not found' }, 404);
    }

    return { data: { deleted: true } };
