    if (!membership) {
      return ctx.json({ error: 'Not a member of this organization' }, 403);
    }

    const where = and(
      eq(schema.emailTemplates.scope, 'organization'),
      eq(schema.emailTemplates.organizationId, organizationId),
      status ? eq(schema.emailTemplates.status, status) : undefined
    );

    const templates = await db.query.emailTemplates.findMany({
      where,
      limit,
      offset,
      orderBy: [desc(schema.emailTemplates.createdAt)],
    });

    return { data: templates };
  },
}));

// Create org template
app.post('/organizations/:organizationId/templates', procedure({
  policy: { auth: 'required', roles: ['admin', 'owner'] },
  input: { 
    params: { organizationId: uuidSchema },
    body: createOrgTemplateSchema 
  },
  successStatus: 201,
  handler: async (ctx) => {
    const db = createDbClient(ctx.env);
    const { organizationId } = ctx.input.params;

    // Verify admin/owner membership
    const membership = await db.query.organizationMemberships.findFirst({
      where: and(
        eq(schema.organizationMemberships.userId, ctx.user.id),
        eq(schema.organizationMemberships.organizationId, organizationId),
        eq(schema.organizationMemberships.status, 'active')
      ),
    });

    if (!membership || !['admin', 'owner'].includes(membership.role)) {
      return ctx.json({ error: 'Admin access required' }, 403);
    }

    const [template] = await db.insert(schema.emailTemplates).values({
      ...ctx.input.body,
      scope: 'organization',
      organizationId,
      creatorId: null,
      createdBy: ctx.user.id,
    }).returning();

    return { data: template };
  },
}));

// ============================================
// CREATOR TEMPLATES
// ============================================

// List creator's own templates
app.get('/creator', procedure({
  policy: { auth: 'required', roles: ['creator'] },
  input: { query: listTemplatesQuerySchema },
  handler: async (ctx) => {
    const db = createDbClient(ctx.env);
    const { page, limit, status } = ctx.input.query;
    const offset = (page - 1) * limit;

    const where = and(
      eq(schema.emailTemplates.scope, 'creator'),
      eq(schema.emailTemplates.creatorId, ctx.user.id),
      status ? eq(schema.emailTemplates.status, status) : undefined
    );

    const templates = await db.query.emailTemplates.findMany({
      where,
      limit,
      offset,
      orderBy: [desc(schema.emailTemplates.createdAt)],
    });

    return { data: templates };
  },
}));

// Create creator template
app.post('/creator', procedure({
  policy: { auth: 'required', roles: ['creator'] },
  input: { body: createCreatorTemplateSchema },
  successStatus: 201,
  handler: async (ctx) => {
    const db = createDbClient(ctx.env);

    const [template] = await db.insert(schema.emailTemplates).values({
      ...ctx.input.body,
      scope: 'creator',
      organizationId: ctx.input.body.organizationId ?? null,
      creatorId: ctx.user.id,
      createdBy: ctx.user.id,
    }).returning();

    return { data: template };
  },
}));

export { app as templateRoutes };
