```typescript
import { eq, and, or, isNull } from 'drizzle-orm';
import type { Database } from '@codex/database';
import { schema, whereNotDeleted } from '@codex/database';
import type { TemplateScope } from '@codex/validation';

export interface TemplateRecord {
  id: string;
  name: string;
  scope: TemplateScope;
  organizationId: string | null;
  creatorId: string | null;
  subject: string;
  htmlBody: string;
  textBody: string;
  status: 'draft' | 'active' | 'archived';
}

export class TemplateRepository {
  constructor(private db: Database) {}

  /**
   * Find a template by name with scope resolution order:
   * 1. Organization template (if orgId provided)
   * 2. Creator template (if creatorId provided and creator is org member)
   * 3. Global template (fallback)
   */
  async findByName(
    name: string,
    options: {
      organizationId?: string;
      creatorId?: string;
    } = {}
  ): Promise<TemplateRecord | null> {
    const { organizationId, creatorId } = options;

    // Try organization template first
    if (organizationId) {
      const orgTemplate = await this.db.query.emailTemplates.findFirst({
        where: and(
          eq(schema.emailTemplates.name, name),
          eq(schema.emailTemplates.scope, 'organization'),
          eq(schema.emailTemplates.organizationId, organizationId),
          eq(schema.emailTemplates.status, 'active'),
          whereNotDeleted(schema.emailTemplates)
        ),
      });
      if (orgTemplate) {
        return orgTemplate as TemplateRecord;
      }
    }

    // Try creator template (if creator is org member - check done in service layer)
    if (creatorId) {
      const creatorTemplate = await this.db.query.emailTemplates.findFirst({
        where: and(
          eq(schema.emailTemplates.name, name),
          eq(schema.emailTemplates.scope, 'creator'),
          eq(schema.emailTemplates.creatorId, creatorId),
          eq(schema.emailTemplates.status, 'active'),
          whereNotDeleted(schema.emailTemplates)
        ),
      });
      if (creatorTemplate) {
        return creatorTemplate as TemplateRecord;
      }
    }

    // Fallback to global template
    const globalTemplate = await this.db.query.emailTemplates.findFirst({
      where: and(
        eq(schema.emailTemplates.name, name),
        eq(schema.emailTemplates.scope, 'global'),
        eq(schema.emailTemplates.status, 'active'),
        whereNotDeleted(schema.emailTemplates)
      ),
    });

    return globalTemplate as TemplateRecord | null;
  }

  /**
   * Get template by ID
   */
  async findById(id: string): Promise<TemplateRecord | null> {
    const template = await this.db.query.emailTemplates.findFirst({
      where: and(
        eq(schema.emailTemplates.id, id),
        whereNotDeleted(schema.emailTemplates)
      ),
    });
    return template as TemplateRecord | null;
  }

  /**
   * Check if user is a member of the organization
   */
  async isOrgMember(userId: string, organizationId: string): Promise<boolean> {
    const membership = await this.db.query.organizationMemberships.findFirst({
      where: and(
        eq(schema.organizationMemberships.userId, userId),
        eq(schema.organizationMemberships.organizationId, organizationId),
        eq(schema.organizationMemberships.status, 'active')
      ),
    });
    return !!membership;
  }
}
```

### 2. Notification Service
File: `packages/notifications/src/services/notifications-service.ts`

```typescript
import { BaseService, type ServiceConfig } from '@codex/service-errors';
import { BrandingSettingsService, ContactSettingsService } from '@codex/platform-settings';
import type { SendEmailInput } from '@codex/validation';
import type { EmailProvider, EmailFrom, SendResult } from '../providers/types';
import { TemplateRepository } from '../repositories/template-repository';
import { renderEmailTemplate, getAllowedTokens } from '../templates/renderer';
import { TemplateNotFoundError } from '../errors';

export interface NotificationServiceConfig extends ServiceConfig {
  emailProvider: EmailProvider;
  brandingService: BrandingSettingsService;
  contactService: ContactSettingsService;
  fromEmail: string;
  fromName?: string;
}

export interface SendEmailResult {
  success: boolean;
  messageId?: string;
  error?: string;
  templateScope: 'global' | 'organization' | 'creator';
}

export class NotificationService extends BaseService {
  private templateRepo: TemplateRepository;
  private emailProvider: EmailProvider;
  private brandingService: BrandingSettingsService;
  private contactService: ContactSettingsService;
  private from: EmailFrom;

  constructor(config: NotificationServiceConfig) {
    super(config);
    this.templateRepo = new TemplateRepository(this.db);
    this.emailProvider = config.emailProvider;
    this.brandingService = config.brandingService;
    this.contactService = config.contactService;
    this.from = {
      email: config.fromEmail,
      name: config.fromName,
    };
  }

  /**
   * Send an email using a template
   */
  async sendEmail(input: SendEmailInput): Promise<SendEmailResult> {
    const { templateName, recipientEmail, recipientName, data, organizationId, creatorId } = input;

    // 1. Resolve template
    const template = await this.templateRepo.findByName(templateName, {
      organizationId,
      creatorId,
    });

    if (!template) {
      throw new TemplateNotFoundError(templateName);
    }

    // 2. Get branding tokens
    const brandTokens = await this.getBrandTokens(organizationId);

    // 3. Merge data with brand tokens (brand tokens cannot be overridden)
    const mergedData = {
      ...data,
      ...brandTokens, // Brand tokens take precedence
    };

    // 4. Render template
    const allowedTokens = getAllowedTokens(templateName);
    const rendered = renderEmailTemplate({
      htmlTemplate: template.htmlBody,
      textTemplate: template.textBody,
      data: mergedData,
      allowedTokens,
    });

    // Log warnings for missing/unknown tokens
    if (rendered.html.missingTokens.length > 0) {
      console.warn(\`[Email] Missing tokens in \${templateName}: \${rendered.html.missingTokens.join(', ')}\`);
    }
    if (rendered.html.unknownTokens.length > 0) {
      console.warn(\`[Email] Unknown tokens in \${templateName}: \${rendered.html.unknownTokens.join(', ')}\`);
    }

    // 5. Render subject (also needs token replacement)
    const subjectResult = renderEmailTemplate({
      htmlTemplate: template.subject,
      textTemplate: template.subject,
      data: mergedData,
      allowedTokens,
    });

    // 6. Send email
    const result = await this.sendWithRetry({
      to: recipientEmail,
      toName: recipientName,
      subject: subjectResult.text.content,
      html: rendered.html.content,
      text: rendered.text.content,
    });

    return {
      ...result,
      templateScope: template.scope,
    };
  }

  /**
   * Get brand tokens from platform settings
   */
  private async getBrandTokens(organizationId?: string): Promise<Record<string, string>> {
    if (!organizationId) {
      // Use defaults for non-org emails
      return {
        platformName: 'Codex',
        logoUrl: '',
        primaryColor: '#000000',
        secondaryColor: '#ffffff',
        supportEmail: this.from.email,
        contactUrl: '',
      };
    }

    try {
      const branding = await this.brandingService.getSettings(organizationId);
      const contact = await this.contactService.getSettings(organizationId);

      return {
        platformName: branding.platformName ?? 'Codex',
        logoUrl: branding.logoUrl ?? '',
        primaryColor: branding.primaryColor ?? '#000000',
        secondaryColor: branding.secondaryColor ?? '#ffffff',
        supportEmail: contact.supportEmail ?? this.from.email,
        contactUrl: contact.websiteUrl ?? '',
      };
    } catch (error) {
      // Fallback to defaults on error
      console.warn(\`[Email] Failed to get brand settings for org \${organizationId}:\`, error);
      return {
        platformName: 'Codex',
        logoUrl: '',
        primaryColor: '#000000',
        secondaryColor: '#ffffff',
        supportEmail: this.from.email,
        contactUrl: '',
      };
    }
  }

  /**
   * Send email with single retry on failure
   */
  private async sendWithRetry(message: {
    to: string;
    toName?: string;
    subject: string;
    html: string;
    text: string;
  }): Promise<SendResult> {
    // First attempt
    let result = await this.emailProvider.send(message, this.from);

    if (!result.success) {
      console.warn(\`[Email] First attempt failed: \${result.error}. Retrying...\`);

      // Single retry (no backoff in Phase 1)
      result = await this.emailProvider.send(message, this.from);

      if (!result.success) {
        console.error(\`[Email] Retry failed: \${result.error}\`);
      }
    }

    return result;
  }

  /**
   * Preview a template with test data (no send)
   */
  async previewTemplate(
    templateId: string,
    testData: Record<string, string> = {}
  ): Promise<{ subject: string; html: string; text: string }> {
    const template = await this.templateRepo.findById(templateId);
    if (!template) {
      throw new TemplateNotFoundError(\`Template ID: \${templateId}\`);
    }

    // Use test brand tokens
    const brandTokens = {
      platformName: 'Preview Platform',
      logoUrl: 'https://example.com/logo.png',
      primaryColor: '#3B82F6',
      secondaryColor: '#1E40AF',
      supportEmail: 'support@example.com',
      contactUrl: 'https://example.com/contact',
    };

    const mergedData = { ...testData, ...brandTokens };
    const allowedTokens = getAllowedTokens(template.name);

    const rendered = renderEmailTemplate({
      htmlTemplate: template.htmlBody,
      textTemplate: template.textBody,
      data: mergedData,
      allowedTokens,
    });

    const subjectResult = renderEmailTemplate({
      htmlTemplate: template.subject,
      textTemplate: template.subject,
      data: mergedData,
      allowedTokens,
    });

    return {
      subject: subjectResult.text.content,
      html: rendered.html.content,
      text: rendered.text.content,
    };
  }
}
